import React from 'react';
import '../css/Records.css';
import { Chrono } from 'react-chrono';
import { useTranslation } from 'react-i18next';

const Records = () => {
  const { t, i18n } = useTranslation();

  const items = [
    {
      title: t('date1'),
      cardTitle: 'شرکت خدمات مهندسی مشانیر',

      cardDetailedText:
        ' برنامه نویس و طراح سایت شرکت خدمات مهندسی مشانیر (درحال حاضر) ',
    },
    {
      title: 'آبان 1397',
      cardTitle: 'خدمت سربازی',

      cardDetailedText:
        ' بیست و یک ماه خدمت سربازی در بخش فناوری اطلاعات و ارتباطات ',
    },
    {
      title: 'بهمن 1396',
      cardTitle: 'سرای محله منطقه 6',

      cardDetailedText: 'آیتی من و پشتیبانی نرم افزاری سرای محله منطقه 6 ',
    },
    {
      title: 'اردیبهشت 1395',
      cardTitle: 'ادمین سایت',

      cardDetailedText: 'تولید محتوا و ادمین سایت دیلی مووی    ',
    },
    {
      title: 'آبان 1393',
      cardTitle: 'دبیرستان سلام ایران زمین',

      cardDetailedText:
        '    ادمین سایت ، آیتی من دبیرستان ، کمک مشاور و مدرس زبان برنامه نویسی',
    },
  ];

  return (
    <div id="Records">
      <div className="recorditem">
        <div className="recordtittle">
          <h2>{t('records')}</h2>
        </div>
        <div className="recordinfo">
          <Chrono
            items={items}
            mode="VERTICAL_ALTERNATING"
            cardHeight="100"
            slideShow="true"
            theme={{
              primary: '#548CFF',
              secondary: 'none',
              cardBgColor: '#ffffff78',
              cardForeColor: 'black',
              titleColor: 'black',
            }}
            slideItemDuration="4000"
          />

          {/* <p>
            {' '}
            مدرسه دبیرستان سلام ایران زمین <span>IT man</span> دو سال و نیم
            مشاور و♥{' '}
          </p>
          <p>یک سال ادمین سایت های فیلم های سینمایی♥</p>
          <p>همکاری با سرای محله منطقه 6♥</p>
          <p>طراحی سایت و ادمین سایت♥</p>
          <p>طراح سایت و برنامه نویس شرکت خدمات مهندسی مشانیر (هم اکنون)♥</p> */}
        </div>
        <div className='recordinfoApp'>
          <ul>
            <li>{t('R1')}</li>
            <li>{t('R2')}</li>
            <li>{t('R3')}</li>
            <li>{t('R4')}</li>
          </ul>
          <h4>{t('R5')}</h4>
        </div>
      </div>
    </div>
  );
};

export default Records;
