import React from 'react';
import '../css/Records.css';
import { useTranslation } from 'react-i18next';
import useSlider from 'use-slider'
import "use-slider/lib/slider.min.css";


const Records = () => {
  const { t, i18n } = useTranslation();

  const items = [
    {
      title: 'اردیبهشت ۱۴۰۰',
      cardTitle: 'شرکت خدمات مهندسی مشانیر',

      cardDetailedText:
        ' برنامه نویس و طراح سایت شرکت خدمات مهندسی مشانیر (درحال حاضر) ',
    },
    {
      title: 'آبان 1397',
      cardTitle: 'خدمت سربازی',

      cardDetailedText:
        ' بیست و یک ماه خدمت سربازی در بخش فناوری اطلاعات و ارتباطات ',
    },
    {
      title: 'بهمن 1396',
      cardTitle: 'سرای محله منطقه 6',

      cardDetailedText: 'آیتی من و پشتیبانی نرم افزاری سرای محله منطقه 6 ',
    },
    {
      title: 'اردیبهشت 1395',
      cardTitle: 'ادمین سایت',

      cardDetailedText: 'تولید محتوا و ادمین سایت دیلی مووی    ',
    },
    {
      title: 'آبان 1393',
      cardTitle: 'دبیرستان سلام ایران زمین',

      cardDetailedText:
        '    ادمین سایت ، آیتی من دبیرستان ، کمک مشاور و مدرس زبان برنامه نویسی',
    },
  ];


  const { ref } = useSlider({
    autoPlay: true,
    loop: true,
    duration: 4000,
    navigation: true,
  })


  return (
    <div id="Records">
      <div className="recorditem">
        <div className="recordtittle">
          <h2>{t('records')}</h2>
        </div>
        <div className="recordinfo">
        <div ref={ref} className='recordMain'>
      <div className='recordItems'>{t('R1')}</div>
      <div className='recordItems'>{t('R2')}</div>
      <div className='recordItems'>{t('R3')}</div>
      <div className='recordItems'>{t('R4')}</div>
      <div className='recordItems'>{t('R5')}</div>
     
    </div>
        </div>
        <div className='recordinfoApp'>
          <ul>
            <li>{t('R1')}</li>
            <li>{t('R2')}</li>
            <li>{t('R3')}</li>
            <li>{t('R4')}</li>
          </ul>
          <h4>{t('R5')}</h4>
        </div>
      </div>
    </div>
  );
};

export default Records;
